### Ajax的使用

#### Ajax封装

```js
let myAjax = (type, url, param, dataType, callback, async) => {
    let xhr = null;
    if (window.XMLHttpRequest) {
        xhr = new XMLHttpRequest();
    } else {
        xhr = new ActiveXObject("Microsoft.XMLHTTP");
    }

    if (type === "get") {
        if (param && param !== "") {
            url += "?" + param;
        }
    }
    xhr.open(type, url, async);

    if (type === "get") {
        xhr.send(null);
    } else if (type === "post") {
        xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
        xhr.send(param);
    }

    if (async) {
        xhr.onreadystatechange = function () {
            if (this.readyState === 4) {
                if (this.status === 200) {
                    let result = null;
                    if (dataType === "json") {
                        result = this.responseText;
                        result = JSON.parse(result);
                    } else if (dataType === "xml") {
                        result = this.responseXML;
                    } else {
                        result = this.responseText;
                    }
                    if (callback) {
                        callback(result);
                    }
                }
            }
        };
    } else {
        if (xhr.readyState === 4) {
            if (xhr.status === 200) {
                let result = null;
                if (dataType === "json") {
                    result = xhr.responseText;
                    result = JSON.parse(result);
                } else if (dataType === "xml") {
                    result = xhr.responseXML;
                } else {
                    result = xhr.responseText;
                }
                if (callback) {
                    callback(result);
                }
            }
        }
    }
};

let obj = {

};
console.log(obj);

let myAjax2 = (obj) => {
    let defaults = {
        type: "get",
        url: "#",
        dataType: "json",
        data: {},
        async: true,
        success: function (result) {
            console.log(result);
        }
    };
    //obj属性覆盖
    //1、 如果有一些属性只存在obj中， 会给defaults中增加属性
    //2、 如果有一些属性在obj和defaults中都存在， 会将defaults中的默认值覆盖
    //3、 如果有一些属性只在defaults中存在， 在obj中不存在，这时候defaults中将保留预定义的属性
    for (let key in obj) {
        defaults[key] = obj[key];
    }

    let xhr = null;
    if (window.XMLHttpRequest) {
        xhr = new XMLHttpRequest();
    } else {
        xhr = new ActiveXObject("Microsoft.XMLHTTP");
    }

    let params = "";
    for (let attr in defaults.data) {
        params += attr + "=" + defaults.data[attr] + "&";
    }
    if (params) {
        params = params.substring(0, params.length - 1);
    }
    if (defaults.type === "get") {
        defaults.url += "?" + params;
    }
    xhr.open(defaults.type, defaults.url, defaults.async);
    if (defaults.type === "get") {
        xhr.send(null);
    }else if (defaults.type === "get") {
        xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
        xhr.send(params);
    }

    if (defaults.async) {
        xhr.onreadystatechange = function () {
            if (this.readyState === 4) {
                if (this.status === 200) {
                    let result = null;
                    if (defaults.dataType === "json") {
                        result = this.responseText;
                        result = JSON.parse(result);
                    } else if (defaults.dataType === "XML") {
                        result = this.responseXML
                    } else {
                        result = this.responseText
                    }

                    defaults.success(result);
                }
            }
        }
    } else {
        if (xhr.readyState === 4) {
            if (xhr.status === 200) {
                let result = null;
                if (defaults.dataType === "json") {
                    result = xhr.responseText;
                    result = JSON.parse(result);
                } else if (defaults.dataType === "XML") {
                    result = xhr.responseXML
                } else {
                    result = xhr.responseText
                }

                defaults.success(result);
            }
        }
    }
};
```

