                let result = null;
                if (defaults.dataType === "json") {
                    result = xhr.responseText;
                    result = JSON.parse(result);
                } else if (defaults.dataType === "XML") {
                    result = xhr.responseXML
                } else {
                    result = xhr.responseText
                }

                defaults.success(result);
            }
        }
    }
};
```

